package function;

public class onehundred {
	public static boolean hun (int num, int num2) {
		return num+num2<100;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println (hun(22,15));
		System.out.println (hun(83,117));
		System.out.println (hun(3,77));
	}

}
